mapper.select:
select s.subject_id as subjectId,sbj.name as subjectName,s.user_id as userId,steam,problem_type as problemType,difficulty as difficultyLevel,score,point_id as  knowledgePoint
from (select subject_id,steam,problem_type,difficulty,score,point_id,user_id,status from blank union all select subject_id,steam,problem_type,difficulty,score,point_id,user_id,status from judgement union all select subject_id,steam,problem_type,difficulty,score,point_id,user_id,status from multiple union all select subject_id,steam,problem_type,difficulty,score,point_id,user_id,status from short_answer union all select subject_id,steam,problem_type,difficulty,score,point_id,user_id,status from single) s left join subject sbj on subject_id = sbj.id


        and s.problem_type = #{problemReqVo.problemType}


        and s.subject_id = #{problemReqVo.subjectId}


        and s.point_id like concat('%', #{problemReqVo.pointId}, '%')


        and s.difficulty = #{problemReqVo.difficultyLevel}


        and s.score = #{problemReqVo.score}

    and s.status = 0
    and sbj.status = 0
